INSERT INTO SYS_DIC_SQL VALUES ('<|<UUID>|>', 'crew_on_board', 'crew_on_board', 'SELECT A.PERSON_ID AS DICVALUE, (CASE WHEN A.PERSON_ID = \'\' OR A.PERSON_ID IS NULL THEN A.PERSON_CNAME ELSE C.USER_CNAME END) AS DICTEXT FROM CREW_ON_BOARD A LEFT JOIN SYS_POST B ON A.POST_ID = B.POST_ID LEFT JOIN SYS_PERSON C ON A.PERSON_ID = C.PERSON_ID WHERE A.IS_DEL = \'0\' AND A.COMPANY_ID = \'<COMPANY_ID>\'', '1', 'AND A.SHIP_ID = \'<param>\'', 'ORDER BY B.POST_CODE', null, null, null, '0', '2017-01-01 00:00:00');
